// BenignFilenames returns a subset of filenames that should not match malware patterns
func BenignFilenames() []string {
	malicious := make(map[int]bool)
	for _, idx := range MaliciousIndices {
		malicious[idx] = true
	}

	var benign []string
	for i, f := range TestFilenames {
		if !malicious[i] {
			benign = append(benign, f)
		}
	}
	return benign
}
