// BenignFilenames returns a subset of TestFilenames that don't match any malware patterns.
// Useful for testing no-match scenarios.
func BenignFilenames() []string {
	malicious := make(map[int]bool)
	for _, idx := range MaliciousIndices {
		malicious[idx] = true
	}

	var benign []string
	for i, f := range TestFilenames {
		if !malicious[i] {
			benign = append(benign, f)
		}
	}
	return benign
}
